import React from "react";
import "../CSS/Content.css";
import BgndImg1 from "../Images/BgndImg1.jpg";
import Favorite from '@material-ui/icons/Favorite';
import clickme6 from '../Images/clickme6.jpg';
function Header(){
    return(
      <div
      class="bg_image"
      style={{
        backgroundImage: 'url('+BgndImg1+')',
        backgroundSize: "cover",
      }}
    >
      <h1 style={{fontStyle:"italic", textAlign:"center", color:"rgba(0,0.5,0.7,0.6)", fontSize:"5rem"}}>Happiest Birthday <br/> dear <br/>Shrinidhi<Favorite style={{'color': "red", height:"100px" ,width:"100px", marginTop:"0px"}}/></h1>
      <div style={{textAlign:"center"}}>
        <div class="click">
      <img height="20%" width="20%" src={clickme6} alt="heart"/>
        </div>
      </div>
      
     </div>
     
    )
}

export default Header;
